            </select>
          </div>
        </div>

        <div className="flex items-center gap-2 px-3 py-1 rounded text-xs font-mono border transition-colors bg-blue-900/20 border-blue-800 text-blue-300">
          <Cloud className={`w-3 h-3 ${backupStatus === 'Syncing...' ? 'animate-bounce' : ''}`} />
          <span>S3 Backup: {backupStatus}</span>
        </div>
      </div>

      <Pipeline step={pipelineStep} testMode={testScenario} />

      <div className="flex-1 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-slate-700 p-4 overflow-y-auto min-h-[350px] max-h-[450px]">
        {messages.length === 0 && (
          <div className="flex items-center justify-center h-full text-slate-500 font-mono text-sm">
            Secure channel established. Ready for transmission.
          </div>
        )}
        <div className="flex flex-col gap-3">
          {messages.map(msg => (
            <div key={msg.id} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`flex items-start gap-2 max-w-[85%] ${msg.sender === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${msg.sender === 'system' ? 'bg-red-500/20 border border-red-400' :
                  msg.sender === 'user' ? 'bg-cyan-500/20 border border-cyan-400' :
                    'bg-violet-500/20 border border-violet-400'
                  }`}>
                  {msg.sender === 'system' ? <AlertTriangle className="w-4 h-4 text-red-400" /> :
                    <User className={`w-5 h-5 ${msg.sender === 'user' ? 'text-cyan-400' : 'text-violet-400'}`} />}
                </div>

                <div className={`px-4 py-2 rounded-lg ${msg.sender === 'system'
                  ? 'bg-red-900/30 border border-red-500/50 text-red-200'
                  : msg.status === 'rejected' || msg.status === 'failed'
                    ? 'bg-red-900/10 border border-red-500/30 text-red-300'
                    : msg.sender === 'user'
                      ? 'bg-cyan-500/20 border border-cyan-400/50'
                      : 'bg-violet-500/20 border border-violet-400/50'
                  }`}>
                  <p className="text-sm font-mono text-slate-200">
                    {msg.encrypted ? 'â€¢â€¢â€¢ ENCRYPTED â€¢â€¢â€¢' : msg.text}
                  </p>
                  {msg.sender !== 'system' && (
                    <div className="mt-1 flex justify-end">
                      {msg.status === 'rejected' ?
                        <span className="text-[10px] text-red-400 flex items-center gap-1"><XCircle className="w-3 h-3" /> Sig Fail</span> :
                        msg.status === 'failed' ?
                          <span className="text-[10px] text-red-400 flex items-center gap-1"><XCircle className="w-3 h-3" /> Send Fail</span> :
                          msg.encrypted ? <Lock className="w-3 h-3 text-slate-500" /> : <CheckCircle className="w-3 h-3 text-emerald-400" />
                      }
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={e => e.key === 'Enter' && sendMessage()}
          placeholder={testScenario !== 'NONE' ? `Running Test: ${testScenario}...` : "Type a secure message..."}
          disabled={processing}
          className={`flex-1 px-4 py-3 bg-slate-800/50 border rounded-lg text-slate-200 placeholder-slate-500 font-mono text-sm focus:outline-none disabled:opacity-50 ${testScenario !== 'NONE' ? 'border-yellow-600/50 focus:border-yellow-500' : 'border-slate-700 focus:border-cyan-400'}`}
        />
        <button
          onClick={sendMessage}
          disabled={processing || !input.trim()}
          className={`px-6 py-3 font-bold rounded-lg transition-all flex items-center gap-2 ${testScenario !== 'NONE'
            ? 'bg-yellow-600 hover:bg-yellow-700 text-white disabled:bg-slate-700'
            : 'bg-cyan-500 hover:bg-cyan-600 text-slate-900 disabled:bg-slate-700 disabled:text-slate-500'
            }`}
        >
          <Send className="w-4 h-4" />
          {testScenario !== 'NONE' ? 'Test' : 'Send'}
        </button>
      </div>
    </div>
  );
};

// 7. NEW COMPARISON WIDGET
const ComparisonWidget: React.FC<{ step: WalkthroughStep }> = ({ step }) => {
  // QRNG Comparison
  const PrngCanvas = useRef<HTMLCanvasElement>(null);
  useEffect(() => {
    if (step !== 0) return;
    const ctx = PrngCanvas.current?.getContext('2d');
    if (!ctx) return;
    let t = 0;
    const animate = () => {
      const imageData = ctx.createImageData(150, 60);
      for (let i = 0; i < imageData.data.length; i += 4) {
        const x = (i / 4) % 150;
        // PRNG Pattern: Sine wave predictable
        const v = (Math.sin((x + t) * 0.1) + 1) * 100;
        imageData.data[i] = v;
        imageData.data[i + 1] = v * 0.5;
        imageData.data[i + 2] = 255 - v;
        imageData.data[i + 3] = 255;
      }
      ctx.putImageData(imageData, 0, 0);
      t += 2;
      requestAnimationFrame(animate);
    }
    animate();
  }, [step]);

  // Kyber Animation State
  const [rsaHealth, setRsaHealth] = useState(100);
  useEffect(() => {
    if (step !== 1) return;
    const interval = setInterval(() => {
      setRsaHealth(prev => Math.max(0, prev - 2)); // RSA cracks fast
    }, 50);
    return () => { clearInterval(interval); setRsaHealth(100); };
  }, [step]);

  if (step === 0) { // QRNG vs PRNG
    return (
      <div className="grid grid-cols-2 gap-4 h-full">
        <div className="bg-slate-900/50 p-3 rounded border border-slate-700 flex flex-col items-center">
          <span className="text-xs font-mono text-slate-400 mb-2">Classical: Pseudo-RNG</span>
          <canvas ref={PrngCanvas} width={150} height={60} className="w-full h-16 rounded border border-slate-600 mb-2 opacity-70" />
          <span className="text-[10px] text-red-400">Predictable Pattern</span>
        </div>
        <div className="bg-slate-900/50 p-3 rounded border border-slate-700 flex flex-col items-center">
          <span className="text-xs font-mono text-cyan-400 mb-2">Quantum: QRNG</span>
          <div className="w-full h-16 rounded border border-cyan-500/50 bg-[url('https://grainy-gradients.vercel.app/noise.svg')] mb-2" />
          <span className="text-[10px] text-cyan-400">True Randomness</span>
        </div>
      </div>
    );
  }

  if (step === 1) { // Kyber vs RSA
    return (
      <div className="grid grid-cols-2 gap-4 h-full">
        <div className="bg-slate-900/50 p-3 rounded border border-red-900/30 flex flex-col gap-2">
          <div className="flex justify-between text-xs font-mono text-slate-400">
            <span>RSA-2048</span>
            <span className="text-red-500">{rsaHealth === 0 ? 'BROKEN' : 'UNDER ATTACK'}</span>
          </div>
          <div className="w-full h-2 bg-slate-700 rounded-full overflow-hidden">
            <div
              className="h-full bg-red-500 transition-all duration-100"
              style={{ width: `${rsaHealth}%` }}
            />
          </div>
          <div className="flex items-center justify-center h-12">
            {rsaHealth === 0 ? <Unlock className="text-red-500 w-8 h-8" /> : <Lock className="text-slate-500 w-8 h-8" />}
          </div>
          <span className="text-[10px] text-slate-500 text-center">Vulnerable to Shor's Algorithm</span>
        </div>
        <div className="bg-slate-900/50 p-3 rounded border border-violet-900/30 flex flex-col gap-2">
          <div className="flex justify-between text-xs font-mono text-violet-400">
            <span>Kyber-1024</span>
            <span className="text-emerald-400">SECURE</span>
          </div>
          <div className="w-full h-2 bg-slate-700 rounded-full overflow-hidden">
            <div className="h-full bg-emerald-400 w-full" />
          </div>
          <div className="flex items-center justify-center h-12">
            <Shield className="text-violet-400 w-8 h-8" />
          </div>
          <span className="text-[10px] text-slate-500 text-center">Lattice-based Immunity</span>
        </div>
      </div>
    );
  }

  if (step === 2) { // Dilithium vs ECDSA
    return (
      <div className="grid grid-cols-2 gap-4 h-full">
        <div className="bg-slate-900/50 p-3 rounded border border-slate-700 flex flex-col items-center justify-between">
          <span className="text-xs font-mono text-slate-400">RSA / ECDSA</span>
          <div className="flex items-end gap-2 h-16 w-full justify-center pb-2 border-b border-slate-700">
            <div className="w-4 bg-slate-600 h-[20%]" title="Key Size" />
            <div className="w-4 bg-red-500 h-[10%]" title="Quantum Security" />
          </div>
          <div className="text-[10px] text-center space-y-1">
            <div className="text-slate-400">Small Keys</div>
            <div className="text-red-400 font-bold">Insecure</div>
          </div>
        </div>
        <div className="bg-slate-900/50 p-3 rounded border border-emerald-900/30 flex flex-col items-center justify-between">
          <span className="text-xs font-mono text-emerald-400">Dilithium</span>
          <div className="flex items-end gap-2 h-16 w-full justify-center pb-2 border-b border-slate-700">
            <div className="w-4 bg-emerald-600 h-[80%]" title="Key Size" />
            <div className="w-4 bg-emerald-400 h-[100%]" title="Quantum Security" />
          </div>
          <div className="text-[10px] text-center space-y-1">
            <div className="text-slate-400">Larger Keys</div>
            <div className="text-emerald-400 font-bold">Quantum Safe</div>
          </div>
        </div>
      </div>
    );
  }
  return null;
}

