import React, { useState, useEffect, useRef } from 'react';
import { Shield, Zap, Key, FileCheck, Send, ArrowRight, ChevronLeft, ChevronRight, Lock, User } from 'lucide-react';

// types.ts
type PipelineStep = 'IDLE' | 'QRNG' | 'KYBER' | 'DILITHIUM' | 'SENDING';
type ViewMode = 'CHAT' | 'WALKTHROUGH';
type WalkthroughStep = 0 | 1 | 2;

interface Message {
  id: string;
  sender: 'user' | 'bob';
  text: string;
  encrypted: boolean;
  timestamp: number;
}

interface InfoCardData {
  title: string;
  role: string;
  pros: string[];
  cons: string[];
  technicalDetail: string;
}

// Pipeline Component
const Pipeline: React.FC<{ step: PipelineStep }> = ({ step }) => {
  const steps = [
    { id: 'QRNG', icon: Zap, label: 'QRNG', color: 'cyan' },
    { id: 'KYBER', icon: Key, label: 'Kyber', color: 'violet' },
    { id: 'DILITHIUM', icon: FileCheck, label: 'Dilithium', color: 'emerald' },
    { id: 'SENDING', icon: Send, label: 'Network', color: 'blue' }
  ];

  return (
    <div className="flex items-center justify-center gap-2 p-4 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-slate-700">
      {steps.map((s, idx) => {
        const Icon = s.icon;
        const isActive = step === s.id;
        const isPast = steps.findIndex(st => st.id === step) > idx;

        return (
          <React.Fragment key={s.id}>
            <div className={`flex flex-col items-center gap-1 transition-all duration-300 ${
              isActive ? 'scale-110' : isPast ? 'opacity-50' : 'opacity-30'
            }`}>
              <div className={`p-3 rounded-full border-2 ${
                isActive 
                  ? `bg-${s.color}-500/20 border-${s.color}-400 animate-pulse shadow-lg shadow-${s.color}-500/50` 
                  : isPast
                  ? `bg-${s.color}-500/10 border-${s.color}-600`
                  : 'bg-slate-800 border-slate-600'
              }`}>
                <Icon className={`w-5 h-5 ${
                  isActive ? `text-${s.color}-400` : isPast ? `text-${s.color}-500` : 'text-slate-500'
                }`} />
              </div>
              <span className={`text-xs font-mono ${
                isActive ? `text-${s.color}-400` : 'text-slate-500'
              }`}>{s.label}</span>
            </div>
            {idx < steps.length - 1 && (
              <ArrowRight className={`w-4 h-4 ${
                isPast ? 'text-emerald-400' : step === s.id ? 'text-cyan-400 animate-pulse' : 'text-slate-600'
              }`} />
            )}
          </React.Fragment>
        );
      })}
    </div>
  );
};

// QRNG Module
const QRNGModule: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const animate = () => {
      const imageData = ctx.createImageData(canvas.width, canvas.height);
      const data = imageData.data;

      for (let i = 0; i < data.length; i += 4) {
        const value = Math.random() * 255;
        data[i] = value;
        data[i + 1] = value;
        data[i + 2] = value;
        data[i + 3] = 255;
      }

      ctx.putImageData(imageData, 0, 0);
    };

    const interval = setInterval(animate, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col items-center gap-4 p-6 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-cyan-500/30">
      <div className="flex items-center gap-2">
        <Zap className="w-6 h-6 text-cyan-400 animate-pulse" />
        <h3 className="text-xl font-bold text-cyan-400 font-mono">Quantum Random Number Generator</h3>
      </div>
      <canvas
        ref={canvasRef}
        width={300}
        height={200}
        className="rounded border-2 border-cyan-500/50 shadow-lg shadow-cyan-500/20"
      />
      <p className="text-sm text-cyan-300/70 text-center font-mono">
        Harvesting quantum entropy from physical processes
      </p>
    </div>
  );
};

// Kyber Module
const KyberModule: React.FC = () => {
  const [step, setStep] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setStep(s => (s + 1) % 3);
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col items-center gap-6 p-6 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-violet-500/30">
      <div className="flex items-center gap-2">
        <Key className="w-6 h-6 text-violet-400 animate-pulse" />
        <h3 className="text-xl font-bold text-violet-400 font-mono">Kyber Key Encapsulation</h3>
      </div>

      <div className="flex items-center justify-between w-full max-w-md">
        <div className="flex flex-col items-center gap-2">
          <div className="w-16 h-16 rounded-full bg-violet-500/20 border-2 border-violet-400 flex items-center justify-center">
            <User className="w-8 h-8 text-violet-400" />
          </div>
          <span className="text-sm font-mono text-violet-300">Alice</span>
        </div>

        <div className="flex-1 flex flex-col items-center gap-2 px-4">
          <div className={`transition-all duration-500 ${step === 1 ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-4'}`}>
            <div className="px-4 py-2 bg-violet-500/20 rounded border border-violet-400 text-xs font-mono text-violet-300">
              Public Key →
            </div>
          </div>
          <div className={`transition-all duration-500 ${step === 2 ? 'opacity-100 -translate-x-0' : 'opacity-0 translate-x-4'}`}>
            <div className="px-4 py-2 bg-violet-500/20 rounded border border-violet-400 text-xs font-mono text-violet-300">
              ← Ciphertext
            </div>
          </div>
        </div>

        <div className="flex flex-col items-center gap-2">
          <div className="w-16 h-16 rounded-full bg-violet-500/20 border-2 border-violet-400 flex items-center justify-center">
            <User className="w-8 h-8 text-violet-400" />
          </div>
          <span className="text-sm font-mono text-violet-300">Bob</span>
        </div>
      </div>

      <div className="text-center">
        <p className="text-sm text-violet-300/70 font-mono">
          {step === 0 && "Generating lattice-based key pair..."}
          {step === 1 && "Exchanging public key..."}
          {step === 2 && "Encapsulating shared secret..."}
        </p>
      </div>
    </div>
  );
};

// Dilithium Step
const DilithiumStep: React.FC = () => {
  const [phase, setPhase] = useState<'signing' | 'verifying' | 'complete'>('signing');

  useEffect(() => {
    const timer1 = setTimeout(() => setPhase('verifying'), 1500);
    const timer2 = setTimeout(() => setPhase('complete'), 3000);
    const timer3 = setTimeout(() => setPhase('signing'), 4500);

    return () => {
      clearTimeout(timer1);
      clearTimeout(timer2);
      clearTimeout(timer3);
    };
  }, [phase]);

  return (
    <div className="flex flex-col items-center gap-6 p-6 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-emerald-500/30">
      <div className="flex items-center gap-2">
        <FileCheck className="w-6 h-6 text-emerald-400 animate-pulse" />
        <h3 className="text-xl font-bold text-emerald-400 font-mono">Dilithium Digital Signature</h3>
      </div>

      <div className="flex flex-col gap-4 w-full max-w-md">
        <div className={`p-4 rounded-lg border-2 transition-all duration-500 ${
          phase === 'signing' 
            ? 'bg-emerald-500/20 border-emerald-400 shadow-lg shadow-emerald-500/30' 
            : 'bg-slate-800/50 border-slate-600'
        }`}>
          <div className="flex items-center gap-2 mb-2">
            <Lock className="w-5 h-5 text-emerald-400" />
            <span className="font-mono text-sm text-emerald-300">SIGNING</span>
          </div>
          <p className="text-xs font-mono text-emerald-300/70">
            Message: "Quantum-secure transmission..."
          </p>
        </div>

        <div className={`p-4 rounded-lg border-2 transition-all duration-500 ${
          phase === 'verifying' 
            ? 'bg-emerald-500/20 border-emerald-400 shadow-lg shadow-emerald-500/30' 
            : 'bg-slate-800/50 border-slate-600'
        }`}>
          <div className="flex items-center gap-2 mb-2">
            <Key className="w-5 h-5 text-emerald-400" />
            <span className="font-mono text-sm text-emerald-300">VERIFYING</span>
          </div>
          <p className="text-xs font-mono text-emerald-300/70">
            Checking signature against public key...
          </p>
        </div>

        <div className={`p-4 rounded-lg border-2 transition-all duration-500 ${
          phase === 'complete' 
            ? 'bg-emerald-500/20 border-emerald-400 shadow-lg shadow-emerald-500/30' 
            : 'bg-slate-800/50 border-slate-600'
        }`}>
          <div className="flex items-center gap-2 mb-2">
            <FileCheck className="w-5 h-5 text-emerald-400" />
            <span className="font-mono text-sm text-emerald-300">VERIFIED ✓</span>
          </div>
          <p className="text-xs font-mono text-emerald-300/70">
            Authenticity confirmed. Message integrity intact.
          </p>
        </div>
      </div>
    </div>
  );
};

// Secure Chat Module
const SecureChatModule: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [pipelineStep, setPipelineStep] = useState<PipelineStep>('IDLE');
  const [processing, setProcessing] = useState(false);

  const getBobResponse = async (userMessage: string, conversationHistory: Message[]) => {
    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 1000,
          system: "You are Bob, a friendly person chatting with your friend through a quantum-secure messaging app. You're a real person, not an AI. Chat naturally like you would with a friend - be casual, warm, and genuine. You can be funny, share opinions, ask questions back, use casual language, and show personality. Occasionally you might casually mention the cool quantum encryption (like 'haha this quantum encryption is pretty neat' or 'glad we're on a secure channel for this') but mostly just chat like a normal friend would. Keep responses conversational and natural, 1-3 sentences usually. Be supportive, interested, and real. You have your own life, experiences, and personality. Never mention being an AI, language model, or assistant.",
          messages: [
            ...conversationHistory.slice(-6).map(msg => ({
              role: msg.sender === 'user' ? 'user' : 'assistant',
              content: msg.text
            })),
            { role: "user", content: userMessage }
          ],
        })
      });

      const data = await response.json();
      const text = data.content
        .filter((item: any) => item.type === "text")
        .map((item: any) => item.text)
        .join("\n");

      return text || "Hey! Yeah I'm here, what's up?";
    } catch (error) {
      console.error("Error getting Bob's response:", error);
      return "Hey! Connection's a bit wonky but I got your message. What's up?";
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || processing) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      sender: 'user',
      text: input,
      encrypted: true,
      timestamp: Date.now()
    };

    const currentInput = input;
    setInput('');
    setMessages(prev => [...prev, userMessage]);
    setProcessing(true);

    // Pipeline animation sequence
    setPipelineStep('QRNG');
    await new Promise(r => setTimeout(r, 800));

    setPipelineStep('KYBER');
    await new Promise(r => setTimeout(r, 800));

    setPipelineStep('DILITHIUM');
    await new Promise(r => setTimeout(r, 800));

    setPipelineStep('SENDING');
    await new Promise(r => setTimeout(r, 600));

    // Decrypt user message
    setMessages(prev => prev.map(m => 
      m.id === userMessage.id ? { ...m, encrypted: false } : m
    ));

    setPipelineStep('IDLE');

    // Get Bob's AI response
    await new Promise(r => setTimeout(r, 500));

    const conversationHistory = [...messages, userMessage];
    const bobResponseText = await getBobResponse(currentInput, conversationHistory);

    const bobMessage: Message = {
      id: (Date.now() + 1).toString(),
      sender: 'bob',
      text: bobResponseText,
      encrypted: true,
      timestamp: Date.now()
    };

    setMessages(prev => [...prev, bobMessage]);

    // Decrypt Bob's message
    await new Promise(r => setTimeout(r, 1500));
    setMessages(prev => prev.map(m => 
      m.id === bobMessage.id ? { ...m, encrypted: false } : m
    ));

    setProcessing(false);
  };

  return (
    <div className="flex flex-col gap-4 h-full">
      <Pipeline step={pipelineStep} />

      <div className="flex-1 bg-slate-800/50 backdrop-blur-sm rounded-lg border border-slate-700 p-4 overflow-y-auto min-h-[400px] max-h-[500px]">
        {messages.length === 0 && (
          <div className="flex items-center justify-center h-full text-slate-500 font-mono text-sm">
            Secure channel established. Start encrypted communication...
          </div>
        )}
        <div className="flex flex-col gap-3">
          {messages.map(msg => (
            <div key={msg.id} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`flex items-start gap-2 max-w-[70%] ${msg.sender === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  msg.sender === 'user' ? 'bg-cyan-500/20 border border-cyan-400' : 'bg-violet-500/20 border border-violet-400'
                }`}>
                  <User className={`w-5 h-5 ${msg.sender === 'user' ? 'text-cyan-400' : 'text-violet-400'}`} />
                </div>
                <div className={`px-4 py-2 rounded-lg ${
                  msg.sender === 'user' 
                    ? 'bg-cyan-500/20 border border-cyan-400/50' 
                    : 'bg-violet-500/20 border border-violet-400/50'
                }`}>
                  <p className="text-sm font-mono text-slate-200">
                    {msg.encrypted ? '••• ENCRYPTED •••' : msg.text}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyPress={e => e.key === 'Enter' && sendMessage()}
          placeholder="Type a secure message..."
          disabled={processing}
          className="flex-1 px-4 py-3 bg-slate-800/50 border border-slate-700 rounded-lg text-slate-200 placeholder-slate-500 font-mono text-sm focus:outline-none focus:border-cyan-400 disabled:opacity-50"
        />
        <button
          onClick={sendMessage}
          disabled={processing || !input.trim()}
          className="px-6 py-3 bg-cyan-500 hover:bg-cyan-600 disabled:bg-slate-700 disabled:text-slate-500 text-slate-900 font-bold rounded-lg transition-all flex items-center gap-2"
        >
          <Send className="w-4 h-4" />
          Send
        </button>
      </div>
    </div>
  );
};

